mapper.select:
SELECT userid, name, email, number
FROM users
WHERE userid = #{userID}

SELECT membershipid, membershipname, price
FROM memberships
WHERE membershipid = #{membershipID}

SELECT usermembershipid, userid, membershipid, status, purchasedate, imp_uid
FROM usermemberships
WHERE usermembershipid = #{userMembershipId}

mapper.update:
UPDATE usermemberships
SET status = #{status}
WHERE imp_uid = #{impUid}
