mapper.select:
SELECT playerid, teamname, playername, position, backnumber, birthdate, height, weight, joiningyear, birthplace, filename, filesize
FROM players
WHERE playerid = #{playerid}

mapper.update:
UPDATE players
SET teamname = #{teamname}, playername = #{playername}, position = #{position}, backnumber = #{backnumber}, birthdate = #{birthdate},
    height = #{height}, weight = #{weight}, joiningyear = #{joiningyear}, birthplace = #{birthplace}, filename = #{filename}, filesize = #{filesize}
WHERE playerid = #{playerid}

mapper.select:
SELECT filename
FROM players
WHERE playerid=#{playerid}
