mapper.select:
SELECT teamname, koreanname, stadiumid, foundingyear, coach, city, introduction, leaguecategory, filename, filesize
FROM teams
WHERE teamname = #{teamname}

mapper.update:
UPDATE teams
SET teamname = #{teamname}, koreanname = #{koreanname}, stadiumid = #{stadiumid}, foundingyear = #{foundingyear}, coach = #{coach},
    city = #{city}, introduction = #{introduction}, leaguecategory = #{leaguecategory}, filename = #{filename}, filesize = #{filesize}
WHERE teamname = #{teamname}
